# database/init.sql
DROP DATABASE IF EXISTS startup_db;
CREATE DATABASE startup_db;
USE startup_db;

CREATE TABLE startups (
    `index` INT AUTO_INCREMENT PRIMARY KEY,
    `Company` VARCHAR(255),
    `Valuation` VARCHAR(50),
    `Date_Joined` DATE,
    `Country` VARCHAR(100),
    `City` VARCHAR(100),
    `Industry` VARCHAR(255),
    `Selector_Investors` TEXT
);

